                var nam = $('#selectYear').val();

                $.ajax({
                    url: '/AdvanceSalary/RenderLoaiDoiTuong',
                    type: 'GET',
                    data: { loai: loai, nam: nam },
                    success: function (data) {
                        $('#bangKetQua').html(data);
                    },
